/** This class holds preferences generated by a FetchBuilder object.*/
class FetchBuilderPrefs(val appContext: Context,
                        val namespace: String,
                        val concurrentLimit: Int,
                        val progressReportingIntervalMillis: Long,
                        val downloadBufferSizeBytes: Int,
                        val loggingEnabled: Boolean,
                        val inMemoryDatabaseEnabled: Boolean,
                        val downloader: Downloader,
                        val globalNetworkType: NetworkType,
                        val logger: Logger,
                        val autoStartProcessing: Boolean,
                        val retryOnConnectionGain: Boolean)